sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   // Get value from Paid Amount field
   var number = parseInt(g_form.getValue('u_paid_amount'), 10);
   var numInWords = convertToWords(number);
   g_form.setValue('u_amount_in_words', numInWords);

   // Function to convert numbers into words (Indian System)
   function convertToWords(n) {
      var num = "Zero One Two Three Four Five Six Seven Eight Nine Ten Eleven Twelve Thirteen Fourteen Fifteen Sixteen Seventeen Eighteen Nineteen".split(" ");
      var tens = "Twenty Thirty Forty Fifty Sixty Seventy Eighty Ninety".split(" ");

      if (n < 20) return num[n];
      if (n < 100) return tens[Math.floor(n / 10) - 2] + (n % 10 ? " " + num[n % 10] : "");
      if (n < 1000) return num[Math.floor(n / 100)] + " Hundred" + (n % 100 == 0 ? "" : " and " + convertToWords(n % 100));
      if (n < 100000) return convertToWords(Math.floor(n / 1000)) + " Thousand" + (n % 1000 != 0 ? " " + convertToWords(n % 1000) : "");
      if (n <= 100000) return convertToWords(Math.floor(n / 100000)) + " Lakh" + (n % 100000 != 0 ? " " + convertToWords(n % 100000) : "");

      return n; // fallback for larger numbers
   }
}
