sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var billnumber = g_form.getValue('u_bill_no');  // sys_id of selected bill

   var ga = new GlideAjax('Autopopulatebilldetails');
   ga.addParam('sysparm_name', 'getbilldetails');
   ga.addParam('sysparm_billid', billnumber);   // pass sys_id to Script Include
   ga.getXMLAnswer(function(response) {
      var res = JSON.parse(response);
      if (res.billamt) {
         g_form.setValue('u_bill_amount', res.billamt);  // set value in form field
      } else if (res.error) {
         g_form.showFieldMsg('u_bill_no', res.error, 'error');
      }
   });
}
